import {createStore} from 'redux';
import { status, soft } from './action/index'

var initialState = {
    status: false,
    sort : {
        by: 'name',
        value: 1
    }
}

var myReducer = (state = initialState, action) => {
    if(action.type === 'TOGGOLE_STATUS') {
        state.status =! state.status;
        return state;
    }
    if(action.type === 'SORFT'){
        var {by, value} = action.soft;
        var { status } = state;
        return {
            status : status,
            soft : {
                by : by, 
                value : value,
            }
        }

    }
    return state;
}

const store = createStore(myReducer); 
console.log('Default:', store.getState());

// thực hiện công việc thay đổi status
var action = {type : 'TOGGOLE_STATUS'};
store.dispatch(action);

// thực hiện công việc sắp xếp từ A-Z
var soft_action = {
    type: 'SORFT',
    soft: {
        by: `name`,
        value: -1
    }    
}; 
store.dispatch(soft_action);
console.log('toggle:', store.getState());
