var initialState = {
    status: false,
    sort : {
        by: 'name',
        value: 1
    }
}

var myReducer = (state = initialState, action) => {
    if(action.type === 'TOGGOLE_STATUS') {
        state.status =! state.status;
        return state;
    }
    if(action.type === 'SORFT'){
        var {by, value} = action.soft;
        var { status } = state;
        return {
            status : status,
            soft : {
                by : by, 
                value : value,
            }
        }

    }
    return state;
}

export default  myReducer;
